import React from 'react'
import { Button, Dimensions, StyleSheet, Text, TouchableOpacity, View } from 'react-native'
import { dayScheduleProps } from '../../../interfaces/interfaces'

const { width } = Dimensions.get('window')

const styles = StyleSheet.create({
  dayText: {
    fontSize: 22,
    color: 'white',
    marginTop: 10,
    textTransform: 'capitalize',
    textAlign: 'center'
  },
  dayView: {
    flex: 1,
    width
  },
  hourView: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    minHeight: 50,
    marginVertical: 5
  },
  modifyButton: {
    backgroundColor: '#14680c',
    height: 'auto',
    padding: 5,
    borderRadius: 3,
    justifyContent: 'center',
    alignItems: 'center'
  },
  modifyButtonText: {
    color: 'white'
  }
})

function DaySchedule ({ item }: dayScheduleProps) {
  return (
    <View style={styles.dayView}>
      <Text style={styles.dayText}>{item.day}</Text>
      <View>
        {item && item.hours.map((hour) => {
          return (
          <View key={performance.now() * Math.random()} style={{
            ...styles.hourView,
            backgroundColor:
            hour.type === 'WOD'
              ? 'blue'
              : hour.type === 'Open Box'
                ? '#c14680c' : '#cb1313'
          }} >
            <View style={{ flex: 1 }}/>
            <Text>{`${hour.startHour}-${hour.finishHour}`}</Text>
            <View style={{ flex: 2 }}/>
            <Text>{hour.type}</Text>
            <View style={{ flex: 2 }}/>
            <TouchableOpacity style={styles.modifyButton}>
              <Text style={styles.modifyButtonText}>Modify</Text>
            </TouchableOpacity>
            <View style={{ flex: 1 }}/>
          </View>)
        })}
      </View>
    </View>
  )
}

export default DaySchedule
